css:
@-moz-document url-prefix("https://habitica.com/groups/myGuilds") {
div.col-md-12 
  {
    column-count: 5;
    -webkit-column-count: 5;
    -moz-column-count: 5;
  }

  .card-link + .card-link 
  {
    margin-left: 0;
  }

  div.col-md-2.badge-column, div.svg-icon.shield, div.col-md-2.cta-container 
  {
    display: none !important;
    width: 0px !important;
  }

  div.category-label, p.summary 
  {
    display: none !important;
  }

  div.col-md-12 div.col-md-8, div.col-md-12 div.col-md-10 
  {
    min-width: 100% !important;
  }

  div.card 
  {
    min-height: 0 !important;
  }

  div.card-body
  {
    padding: 10px !important;
  }

  div.col-md-12 a, div.col-md-12 h3 
  {
    margin-top: -5px !important;
    margin-bottom: -5px !important;
  }
}
