css:
@-moz-document domain("www.dndbeyond.com") {
body {
    background: #1c1c1c;
}
#site .p-comment-header{
  background:#888888!important;  
}

div.forum-post div.forum-post-content div.p-comment-user-background{
    background:#181818!important;
}      

ul.p-comment-stats li,.p-comment-edited{
    color:white!important;
}

ul.p-comment-stats li span,.user-role-registered-users, .comment .source-quote .quote-source{
    color:#fafafa!important;
}                                          
div.forum-post-body blockquote.source-quote, .quote-source{
     background: #282828!important;
}

.forum-post-body-container, div.forum-post-content, div.forum-post-content div.p-comment-wrapper {
    border-color: lightgrey;
    background: black;
    background: #282828;
    color:#fafafa;
}
body.body-forum .single-column #content .p-forum-a .p-comment-post p, body.body-user .single-column #content .p-forum-a .p-comment-post p, body.body-devtracker .single-column #content .p-forum-a .p-comment-post p {
    color: #fafafa;
}

body.body-forum .single-column #content .p-forum-a .listing-container .p-comment-wrapper .p-comment-footer .p-comment-actionsUser .user-action span, body.body-user .single-column #content .p-forum-a .listing-container .p-comment-wrapper .p-comment-footer .p-comment-actionsUser .user-action span, body.body-devtracker .single-column #content .p-forum-a .listing-container .p-comment-wrapper .p-comment-footer .p-comment-actionsUser .user-action span{
    color:#fafafa;
}

.comment .forum-post-body-content .spoiler {
    border-color: lightgrey;
    background: black;
}

#site div.p-forum table.listing-forum-thread td, #site .forum-thread-row .col-thread .title{
    background: #303030!important;
    color: #fafafa!important;
}

#site .col-count a, #site th.b-table-heading a{
	color:#fafafa!important;
}

.content-container h1, .content-container h2, .content-container h3, .content-container h4, .content-container h5, .content-container h6,div, p, span, table, td, tr, th, .p-comment-sig  a {
    color:#fafafa;
}
#site a:active, #site a:visited{
    color:#F5F5F5;
}

#site .post-excerpt-content a, #site .post-excerpt-meta span, #site .post-excerpt-description{
    color:#fafafa!important;
}
.comment .p-comment-content blockquote {
    background: gray;
}
}
