css:
@-moz-document domain("en-forum.guildwars2.com"), domain("es-forum.guildwars2.com"), domain("fr-forum.guildwars2.com"), domain("de-forum.guildwars2.com") {
html {
    background-color: #1a1a1a !important;
    scrollbar-color: #444 #151515;
    filter: invert(1) hue-rotate(200deg) saturate(1.5);
}

body
{
    filter:brightness(0.9);
}

img,iframe {
    filter: invert(1) hue-rotate(-200deg) brightness(1.06) saturate(0.8) !important;
}

.Message.userContent {
    filter: invert(1);
    color: #cbcbcb;
}

.Message.userContent img,.Message.userContent iframe{
    filter: invert(0) hue-rotate(-200deg)  !important;
}

.Message.userContent h1,.Message.userContent h2,.Message.userContent h3 {
    color: white;
}

#scroll-top {
    display: none;
}


}
