css:
@-moz-document domain("www.strava.com") {
#fitness-freshness .line-chart-fitness {
    stroke: #5081a5
}
#fitness-freshness .line-chart-fitness-fill {
    fill: #b8c9d6;
    opacity: .2;
}
#fitness-freshness .line-chart-freshness {
    stroke: #f754d9;
}
#fitness-freshness .line-chart-form {
    stroke: #f1ad30;
}

.fitness-data ul.list-stats li:nth-child(1) .toggle-containers {
   color: #5081a5 
}
.fitness-data ul.list-stats li:nth-child(2) .toggle-containers {
   color: #f754d9 
}
.fitness-data ul.list-stats li:nth-child(3) .toggle-containers {
   color: #f1ad30 
}
}
