css:
/*-------
Auteur : Bowain
Version : Bêta
Site : ZoOrigin
URL : https://www.zoorigin.com/
Notes : Thème non fini
-------*/
@-moz-document domain("zoorigin.com") {

body {
    background-color: #17202A;
    color: white;
}

/* Haut de page */

.header {
    background-color: #10161E;
    border-bottom-color: #121518 !important;
}

.header-hidden {
    background-color: #090D11 !important;
}

.header-upper {
    background-color: #0D1118!important;
}

/* Bas de page */

#footer {
    background-color: #10161E;
    border-top-color: #344559 !important;
}

.bg-inverse-dark {
    background-color: #090D11 !important;
}

.bg-primary {
    background-color: #10161E !important;
}

/* Menu */

.navbar-nav>.nav-item>.nav-link:hover {
    color: #C4C3C3 !important;
}

body #header-hidden-link a.show-hide {
    background-color: #212D3D;
}

/* Articles */

.timeline-stacked .timeline-item, .timeline.timeline-stacked .timeline-item {
    background-color: #10161E;
    border-top-color: #10161E;
    border-left-color: #10161E;
    border-right-color: #10161E;
    border-bottom-color: #2A3B50 !important;
}

.timeline-breaker  {
    background-color: #10161E;
}
/* Loading */

.page-loader {
    background-color: #10161E !important;
}

/* popup */

.modal-header {
    background-color: #10161E;
    border-bottom-color: #17202A;
}

.modal-body {
    background-color: #17202A;
}

.modal-footer {
    background-color: #10161E;
    border-top-color: #17202A;
}



.title-divider span {
    background-color: #17202A;
}

.bg-faded {
    background-color: #10161E !important;
}

.post-to-post-pager {
    background-color: #17202A;
    border-top-color: #17202A;
    border-bottom-color: #17202A;
}

.form-control, .form-control:focus {
    background-color: #10161E;
}

.timeline {
    background-color: #10161E;
}









.cke_top, .cke_bottom {
    background-color: #10161E;
    border-top-color: #344559;
    border-bottom-color: #344559;
}

.cke_chrome {
    border-color: #344559;
}
.bootstrap-switch>div>span.bootstrap-switch-default {
    background-color: #10161E !important;
}
.bootstrap-switch-default {
    background-color: red !important;
}

.nav-section-menu .nav .active .nav-link, 
.nav-section-menu .nav .active .nav-link:hover, 
.nav-section-menu .nav .nav-link.active, 
.nav-section-menu .nav .nav-link.active:hover, 
.nav-section-menu.nav .active .nav-link, 
.nav-section-menu.nav .active .nav-link:hover, 
.nav-section-menu.nav .nav-link.active, 
.nav-section-menu.nav .nav-link.active:hover {
    background-color: #10161E;
}

.nav-section-menu .nav .nav-section-menu .nav-link, 
.nav-section-menu.nav .nav-section-menu .nav-link {
    background-color: #10161E;
    color: white;
}
}
