css:
@-moz-document domain("shiza-project.com") {
body {
	background : #2f2e2e;
    color:#fff;
}

.bg-white,.author, .list-group a, .wrapper-release, .accordion .panel-body
{
	background : #1a1919;

}

.author:hover, .list-group a:hover,.card
{
	background : #3b3a3a
}

 .grad-primary, .grad-success
{
	background : #363636
} 


.secondary-nav li a,.card-title,.com .all,.list-group a p , .form-control, .input-group-addon,.card .desc,.card,.media-title small
{
	color : #d2caca
}


.pagination > li > a, .pagination > li > span
{	font-size : 20px
}

.secondary-nav li a{ font-size : 14px}

.footer ul li a, .footer-brand,.description,.com small,div:nth-child(2) > div:nth-child(1)
{
	color : #fff
}

.slider
{
	display : none
}

 
.zebra p:nth-child(2n+1),.secondary-nav li a:hover,.messages
{
	background : #2f2e2e
}

 
.form-txt {
	border: 1px solid #606060;
	background: #1a1919;
	color: #bec1c3;
}
.card-content {
	position: relative;
	padding: 15px 20px 23px;
	background-color: #2f2e2e;
}
.grad-primary, .grad-success {
	background: linear-gradient(60deg, #2ebcfc, #03a9f4);
}
 
.grad-info {
	background: linear-gradient(60deg, #2d695a, #174036);
}
#subscription {
	margin-top: 10px;
	position: relative;
	background-color: #2C7765;
}
.color-warning {
	background-color: #a06315;
}
.color-success {
	background-color: #2c692e;
}
.zebra p time
{
	background-color: #056d9c;
}
.grad-warning {
	background: linear-gradient(60deg, #915e1e, #784300);
}
.relstatus
{
	background: rgba(94, 47, 101, 0.9);
}
.secondary-nav li a.active
{
	background-color: #641b70;
}
.pagination > .active > a, .pagination > .active > a:focus, .pagination > .active > a:hover, .pagination > .active > span, .pagination > .active > span:focus, .pagination > .active > span:hover
{
	background-color: #6a1a78;
}
.grad-primary, .grad-success {
	background: linear-gradient(60deg, #257aa1, #065477);
}

.btn.btn-primary, .btn.btn-primary:hover, .btn.btn-primary:focus, .btn.btn-primary:active, .btn.btn-primary.active, .btn.btn-primary:active:focus, .btn.btn-primary:active:hover, .btn.btn-primary.active:focus, .btn.btn-primary.active:hover, .open > .btn.btn-primary.dropdown-toggle, .open > .btn.btn-primary.dropdown-toggle:focus, .open > .btn.btn-primary.dropdown-toggle:hover, .navbar .navbar-nav > li > a.btn.btn-primary, .navbar .navbar-nav > li > a.btn.btn-primary:hover, .navbar .navbar-nav > li > a.btn.btn-primary:focus, .navbar .navbar-nav > li > a.btn.btn-primary:active, .navbar .navbar-nav > li > a.btn.btn-primary.active, .navbar .navbar-nav > li > a.btn.btn-primary:active:focus, .navbar .navbar-nav > li > a.btn.btn-primary:active:hover, .navbar .navbar-nav > li > a.btn.btn-primary.active:focus, .navbar .navbar-nav > li > a.btn.btn-primary.active:hover, .open > .navbar .navbar-nav > li > a.btn.btn-primary.dropdown-toggle, .open > .navbar .navbar-nav > li > a.btn.btn-primary.dropdown-toggle:focus, .open > .navbar .navbar-nav > li > a.btn.btn-primary.dropdown-toggle:hover {
	background-color: #61176e;
	color: #FFFFFF;
}

.card-torrent .btn {
	margin: 5px 0;
	background: #138106;
}

.zebra .spoiler-title {
	margin-top: 15px;
	background: #535353;
}
a {
	background-color: transparent;
	-webkit-text-decoration-skip: objects;
	text-decoration: none;
	color: darkgray;
}

.nav > li > a:focus, .nav > li > a:hover, .nav > li > a.active {
	background-color: transparent;
	border-radius: 4px;
}

*, ::before, ::after {
	margin: 0;
	padding: 0;
	-webkit-box-sizing: inherit;
	box-sizing: inherit;
	color: gray;
}
}
