css:
@-moz-document domain("fronter.com") {
* {
    font-family: Helvetica, sans-serif;
    transition: all 0.2s ease;
    color: #ccc;
}

#loginpage {
    background-image: none;
    background-color: #1a1a1a;
}

select.login {
    background-image: none;
    background-color: #2b2b2b;
}

body {
    padding: 0;
    background-image: none;
    background-color: #1a1a1a;
}

html.top_frame_html {
    margin-top: 5px;
}

.content-header2 > p > * > * > * > * > * > * {
    color: #ccc;
    font-weight: 100;
    font-size: 16px;
}

.content-header2 > p > * > * > * > * > * {
    color: #ccc !important;
    font-weight: 100;
    font-size: 16px;
}

.content-header2 > p > * > * > * > * {
    color: #ccc !important;
    font-weight: 100;
    font-size: 16px;
}

.content-header2 > p > * > * > * {
    color: #ccc !important;
    font-weight: 100;
    font-size: 16px;
}

.content-header2 > p > * > * {
    color: #ccc !important;
    font-weight: 100;
    font-size: 16px;
}

.content-header2 > p > * {
    color: #ccc !important;
    font-weight: 100;
    font-size: 16px;
}

.content-header2 > p {
    color: #ccc !important;
    font-weight: 100;
    font-size: 16px;
}

.carousel-button {
    display: none;
}

#shortcut-tabs-list {
    left: 0;
}

#top-row {
    background-image: none;
}

#bottom-row {
    background-image: none;
}

#shadow-separator-wrapper {
    display: none;
}

table.simple-content-frame tr.content-container {
    background: #1a1a1a;
}

a {
    color: #F99654 !important;
}

a > u > font {
    color: #F99654 !important;
}

.clear-white-dashed {
    display: none;
}

.abstract {
    color: #ccc;
}

.tab-action, .tab-link {
    color: #ccc !important;
}

.open-menu-button .dropdown-title-container {
    background-image: none;
    background-color: #2b2b2b;
}

.dropdown-title {
    color: #ccc;
    background-image: none !important;
    padding-right: 0 !important;
}

.dropdown-title::after {
    content: " â–¼";
    color: #ccc;
    background-image: none;
}

.dropdown-menu {
    background-image: none;
    background-color: #2b2b2b;
    border: none !important;
}

.dropdown-content-header {
    color: #ccc;
}

.dropdown-content {
    border-left: solid 1px #ccc;
}

.sprite-title {
    color: #ccc;
}

#account-tools-menu-button {
    top: 23px;
}

#navbar-minimise-button-wrapper {
    display: none;
}

table.content-frame-header {
    background-image: none;
}

.content-tab-full-width {
    background-color: #2b2b2b;
}

div#custom-content-footer-container {
    display: none;
}

#room-tool-list-wrapper {
    padding-top: 0;
}

.news-display-left > a > img {
    max-height: 100px;
}

.fronterjq-tab {
    background-image: none !important;
    background-color: #2b2b2b;
}

.fronterjq-tab.selected {
    border-bottom: 3px solid #F99654;
}

.calendar-element {
    display: none;
}

.room-tool-container:hover .text-button-left-padding, .room-tool-container:hover .text-button-right-padding, .room-tool-container.selected .text-button-left-padding, .room-tool-container.selected .text-button-right-padding, .room-tool-container.selected:hover .text-button-left-padding, .room-tool-container.selected:hover .text-button-right-padding, .room-tool-container:hover .text-button-middle-padding, .room-tool-container.selected .text-button-middle-padding, .room-tool-container.selected:hover .text-button-middle-padding {
    background-image: none;
    background-color: #2b2b2b;
}

.menu-button > * {
    color: #000 !important;
}

.room-tool-list {
    padding: 0;
    margin: 2px 0 0 0;
}

.room-tool-container {
    margin-bottom: 0;
}

.label {
    color: #ccc !important;
}
}
