css:
/* Standard Theme */
@-moz-document domain("mspfa.com") {
    body {
        background-color: #535353;
        color: #000000;
    }
    #main {
        background-color: #c6c6c6;
    }
    #flashytitle {
        background-color: #eeeeee;
    }
    nav {
        background-color: #000000;
        color: #ffffff;
    }
    nav .heart {
        background-image: url("http://mspfa.com/images/candyheart.png");
    }
    #dialog {
        background-color: #eeeeee;
    }
    #dialog > div:nth-child(1) {
        background-color: #c6c6c6;
    }
    .mspfalogo {
        border-right-color: #b8b8b8;
        background-color: #eeeeee;
    }
    table.container {
        border-color: #c6c6c6;
        background-color: #eeeeee;
    }
    table.container > tbody > tr > td, table.container > tbody > tr > th {
        border-color: #c6c6c6;
    }
    #slide {
        background-color: #eeeeee;
    }
    #info > span > .spoiler.open {
        background-color: #eeeeee;
    }
}
