css:
@-moz-document domain("www.friskyradio.com") {
#pageWrap {
	width: auto;
	background: url(https://www.friskyradio.com/img/page-wrap-bg.png) repeat-y 0;
	background-size: cover;
}

#pageWrap > div, .show-episode, .episodes-block {
	display: flex;
	flex-wrap: wrap;
	width: 100%;
}

section.page-content {
	flex: 1;
}

div.list-wrap {
	width: auto;
}

.f-player-bar {
	display: none;
}

.f-view {
	position: static;
}

.latest-episode-item {
	width: 100%;
}

.episode-item {
	flex: 1;
}

.searchresults-list-wrap ul li {
	margin: 1em !important;
}

.jq-searchresults-list {
	margin-left: -1em;
}

body {
	overflow: auto;
}

/* Home page */

.dl-wrap, .nivoSlider, section.page-outof-content {
	width: 100%;
}

section.schedule {
	width: 100%;
}

div.top-list-wrap {
	width: auto;
}

ul.top10-list {
	display: flex;
	width: 100%;
	flex-wrap: wrap;
}

ul.top10-list li.top-list {
	flex: 1;
	width: auto;
	padding: 1em;
}

.artist-of-week {
	width: 100%;
}

/* Show list */

div.list-pagination, div.list-pagination-search {
	width: 100%;
}

}
