@-moz-document domain("rabb.it") {
 
  .toolbar, .banner {
    display:none;
    visibility: hidden;
  }
  
  .content {
    margin-top: 0px!important;
    height: 100%!important;
  }
  
  .social {
    margin-top: 0px!important;
    height: 100%!important;
    top: 0px!important;
  }
  
  .socialLayout::before {
    background-color: black;
  }
  
}
