css:
@namespace url(http://www.w3.org/1999/xhtml);@-moz-document domain("imgoat.com")
{
	body
	{
		background-color: #303233 !important;
		color: #FFF;
	}
	.masterbar
	{
		background-color: #303233 !important;
	}
	.nav > li > a, .nav > li > a:focus
	{
		background-color: #303233;
		border: 1px solid #fff !important;
		border-bottom-color: transparent;
		border-radius: 4px 4px 0 0 !important;
	}
	.nav-tabs > li.active > a,
		.nav-tabs > li.active > a:hover,
		.nav-tabs > li.active > a:focus
	{
		background-color: #5E6061 !important;
		border: 1px solid #fff !important;
		border-bottom-color: transparent;
		color: #fff !important;
		cursor: default;
		outline: none;
	}
	.nav > li > a:hover
	{
		background-color: #5E6061 !important;
	}
	#navbar
	{
		background-color: #303233 !important;
	}
	#navbar ul li a small
	{
		color: #FFF !important;
	}
	.page-header h1 small
	{
		color: #fff !important;
	}
	a
	{
		color: #48A5D4;
	}
	a:hover
	{
		color: #8FD4F7;
	}
	.ajax-upload-dragdrop
	{
		background-color: #303233 !important;
		border: 1px solid #FFF !important;
	}
	.thumbnail
	{
		background-color: #303233 !important;
	}
}
