css:
@-moz-document domain(community.wanikani.com)
{

.poster.trigger-user-card[title="WaniMekani"]::after,
.trigger-user-card.main-avatar[data-user-card="WaniMekani"]::after {
    background-color: rgb(240,240,240);
    content: '?';
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;}

.trigger-user-card.main-avatar[data-user-card="WaniMekani"]::after {
    border-radius: 12px;
    width: 24px;
    height: 24px;
    bottom: -10px;
    right: -8px;}

.poster.trigger-user-card[title="WaniMekani"]::after {
    border-radius: 8px;
    width: 16px;
    height: 16px;
    bottom: -2px;
    right: 0;
    font-size: 0.6em;}

}
