css:
@-moz-document url-prefix("https://profitmaximiser.co.uk/mastermind/betfairrenegade/") {
/* Alternative Scroll Bar Slider Color */
::-webkit-scrollbar-thumb
{
  background: #f5e07c !important;
}

/* Container Background */ 
#scontentr_inner {
    background: #272727;
}
#main_con_txt_c {
    background-color: #191919;
}

/* Main menu coloring */
#bottom_site_menu_wrap {
    background: none !important ;
    background-color: #191919 !important ;
}

/* Header Image Padding */
#sheader > img {
    padding-top: 40px;
}

/* Table Headers */
.accent-color, header h1, header h2, table th {
    color: #f5e07c;
}

/* Upcoming Games Table */ 
.table-striped tbody > tr:nth-child(odd) > td, .table-striped tbody > tr:nth-child(odd) > th {
    background-color: #191919;
}
.table-striped tbody > tr:nth-child(evem) > td, .table-striped tbody > tr:nth-child(even) > th {
    background-color: #272727;
}

/* Rating Color */ 
.label-important, .badge-important {
    color: #282828;
    background-color: #f5e07c;
}
/* BR Odds */
.label-yellow, .badge-yellow {
    color: #282828;
    background-color: #f5e07c;
}

/* Rating Calculator */ 

/* Header */ 
#scontentr_inner h1 {
    color: #ffffff;
    border-bottom: 2px solid #f5e07c;
    margin-bottom: 10px !important; 
}
/* Rating Calc Inputs */
#scontentr_inner > form > table > tbody > tr:nth-child(1) > td:nth-child(2) > input[type="text"]{
    background: none !important ;
    color: #fff !important ;
}
#scontentr_inner > form > table > tbody > tr:nth-child(2) > td:nth-child(2) > input[type="text"]{
    background: none !important ;
	color: #fff !important 
}
#scontentr_inner > form > table > tbody > tr:nth-child(3) > td:nth-child(2) > input[type="text"]{
    background: none !important ;
	color: #fff !important 
}

/*Calculate Button */
button[type=submit], input[type=submit] {
    color: #191919;
    background-color: #f5e07c;
}


}
