css:
@-moz-document url-prefix("https://profitmaximiser.co.uk/mastermind/evmax") {
/*Titles*/
.title h1, .faq_title {
    color: #ff0000 !important;
}

/* Intput Coloring */
.input-group .form-control, .input-group-addon, .input-group-btn, input.edit_middle, input[type="number"], .dropdown-toggle, #machine_search_text {
    color: #fff !important;
    background-color: #232322 !important;
}


/* FAQ Divider */ 
.divider_grey {
    background: #ff0000 !important;
}

/* Alternative Scroll Bar Slider Color */
::-webkit-scrollbar-thumb {
    background: #ff0000;
}
/* Menu Item Hover */ 
.menu ul li:hover, .menu ul li.active {
    background: #ff0000 !important;
}
/* Single Tier Calculator Button Coloring */
.btn-default:hover, .btn-default.active { 
    background: #ff0000;
    color: #fff;
}
/* Green Buttons */ 
#run_button, #add_row, #search_button {
    background: #2d9626;
    color: #fff;
}
/* Red Buttons */ 
#cancel_button, #delete_row, #reset_button {
    color: #fff;
}
/* Simulator Reasults */
.green_text {
    color: #2d9626;
}
/* Table Headers */
th {
    background-color: #232322 ;
    color: #fff ;
    border-left: 1px solid #fff !important;
    border-right: 1px solid #fff !important;                                        
}
}
