css:
@-moz-document regexp("^https?:\/\/www\.unrealengine\.com\/bing-search.*") {


/*===========================  Search results start  ================================*/ 
    body {
        background-color: #333;
        color: #ccc;
    }
    
    input {
        background-color: #333;
    }
    
    #content > h1 {
        margin-top: 10px;
    }
    
    #content .bing-search-box {
        margin-top: 0;
    }
    
    #content .bing-search-input {
        color: #ccc;
    }
    
    
    #content .bing-search-result-item {
        margin: 0.5em 1em;
        padding: 0.5em;
        background-color: #393939;
        border-radius: 4px;
    }
    
    #content .bing-search-type {
        height: auto;
        text-decoration: none;
    }
    
    #content .bing-search-type > ul {
        display: flex;
        flex-wrap: wrap;
    }
    
    #content .bing-search-type .bing-search-type-item {
        float: none;
    }
    
    
    
    
    
    a:focus,
    .search-page :focus {
        outline: 1px dotted #ccc !important;
    }
   
    .search-page .bing-displayUrl {
        color: #808080 !important;
    }
    
    .search-page .bing-search-content {
        background-color: #333 !important;
    }
    
    .search-page .bing-title a {
        border-bottom: none;
    }
    
    .search-page .bing-displayUrl:hover,
    .search-page .bing-title a:hover {
        text-decoration: underline !important;
    }
    
    .search-page .bing-displayUrl:visited {
        color: #958167 !important;
    }
   
    #content .bing-search-type .bing-search-type-item:hover {
        border-color: #fff;
    }
    
    #content .bing-search-type .bing-search-type-item:hover a {
        color: #fff !important;
    }
    
    
    
    
    
/*============================  Search results end  =================================*/
}
