css:
@-moz-document url-prefix("https://profitmaximiser.co.uk") {
/* Current Day */
.row_days div.current, .row_days div.current:hover {
    background: #941700 !important;
}

.row_days > div + div {
    background-color: #941700;
}

.row_title > div, .row_days > div {
    background-color: #941700;
}

/* Current day unchecked offer color */
.row_days div.current > a, .row_days div.current > span, .row_days div.current:hover > a, .row_days div.current:hover > span {
    color: #fff !important;
}

/* Day Hover Color */
.row_days div.actif:hover {
    background: #941700 !important;
}

/* Inactive day hover color */
.row_days div.day_disabled:hover {
    background: #941700;
}

/* Day Text Hover Color */
.row_days div:hover > a:hover {
    background: #c32e11 !important;
}

/* Unchecked offer on unactive day text color */
.row_days div > a {
    color: #fff !important;
}

/* Uncheck offer hover text color*/
.row_days div:hover > a, .row_days div:hover > span {
    color: #fff !important;
}

/* Days of the week */
.row_title > div, .row_days > div {
    color: #fff;
}
}
