css:
@-moz-document url("https://github.com/index.html"), url("https://github.com/"), url("https://www.github.com/index.html"), url("https://www.github.com/"), regexp("https?://(www\.)?github\.com/dashboard.*") {
.news .text-bold {
    font-weight: 400 !important;
}

.news .link-gray-dark {
    color: #2f7bc7 !important;
}

.news .fork,
.news .create,
.news .push,
.news .watch_started,
.news .public {
    font-size: 0.8rem;
}

.news .push div.border.rounded-1.p-3.my-2,
.news .follow div.border.rounded-1.p-3.my-2,
.news .fork div.border.rounded-1.p-3.my-2,
.news .create div.border.rounded-1.p-3.my-2,
.news .watch_started div.border.rounded-1.p-3.my-2,
.news .public div.border.rounded-1.p-3.my-2 {
    display: none;
}
}
