css:
@-moz-document domain(ark-servers.net) {
	.content-box {
		background: black !important;
		color: white !important;
	}
	h2 {
		color: white !important;
	}
	.well {
		background-color: black !important;
		color: white !important;
	}
	.clearfix {
		color: black !important;
	}
	.pagination.pull-right a {
		background-color: black !important;
	}
	.fa-lock:before {
		color: red !important;
	}
	h4 {
		color: white;
	}
	html {
		background: black;
	}
	.form-search .input-append .search-query {
		background-color: black !important;
		color: white !important;
		border-color: black !important;
	}
	.btn {
		background: black !important;
		color: white !important;
		border-color: black !important;
	}
	.active {
		
		color: white !important;
	}
	.disabled {
		background: black !important;
		color: white !important;
	}
	.breadcrumb { background-color: rgb(78, 78, 78) !important; }
	.breadcrumb a { text-shadow: none !important; }
}
