css:
@-moz-document domain("deutsche-arkserver.de") {
html, body {
	background-color: black !important;
	color: white !important;
}
.sticky-footer{
	background-color: #1f1f1f !important;
	color: white !important;
   
}
.vip-shadow {
   background-color: black !important;
}
.panel-body {
   background-color: black !important;
}
.alert-info {
	background-color: #102d3c !important;
}
button.close {
	color: red !important;
}
.close {
	font-size: 35px !important;
   text-shadow: 3px 3px 4px #f66 !important;
}
.col-md-2 center {
    box-shadow: 4px 4px 20px 8px rgba(255, 255, 255, 0.13);
    border-radius: 8px;
    padding-top: 13px;
    border: 2px red;
    background-color: rgba(255, 0, 0, 0.39);
}
/* hmmm */
.panel-body {
   background: #191919 !important;
}
/* Hoster: Erhalt des Steamlogos > fix: png zu jpg */
.panel-body img {
   background: white !important;
	border-radius: 4px !important;
   border: 1px solid black !important;
}
.vip-shadow {
    box-shadow: -4px 4px 20px 8px rgba(153, 51, 255, 0.7) !important;
}
.form-control {
    color: #fff !important;
    background-color: rgb(125, 125, 125) !important;
}
.navbar-static-top {
    background: #171717 !important;
}
.glyphicon {
    color: white !important;
}
.list-group-item {
	background-color: #191919 !important;
	color: white !important;  	
}
a.list-group-item {
    color: #fff !important;
}
}
