css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kanji.koohii.com") {
  .cj-k:hover { font-family: "KanjiStrokeOrders" !important; }

  #my-story .kanji { position:relative; }
  #my-story .cj-k:hover {
    /* make it larger */
    display:block; position:absolute; left:-50px; top:-50px; width:200px; height:200px; padding:0.1em; margin:0;
    box-shadow:0 0 5px rgba(0,0,0,0.5); border-radius:5px;
    font-size:150px; line-height:1em;
    background:#fff; z-index:10000000;
  }

}
