css:
@-moz-document url-prefix("https://www.baidu.com"){
#s_ctner_contents {
	/*
    border:1px solid rgba(233,234,239);
 	*/
 border:1px solid gray;

   background-color:rgb(233,234,239);
}

#s_content_100 {
	   background-color:rgba(233,234,239,0.5);
	   font-family: "Monaco", "Consolas", "STXingkai";
	  

}

#s_mancard_main {
	   background-color:rgba(233,234,239,0.5);
}

.s-ctner-menus .s-menu-mine .mine-text {
	font-size:large;
	 font-family: "Monaco", "Consolas", "STXingkai";
}

/*分类主题字*/
.s-block-nav .dir-item .dir-name .name-text {
	font-size:x-large;
	 text-shadow: 0.05em 0.05em 0.3em #999999 !important;

}
/*每个主题里面添加的链接*/
.s-block-nav .border-for-item .nav-text .nav-text-content {
	font-size:x-large;
	 text-shadow: 0.05em 0.05em 0.2em #999999 !important;
  font-family: "Monaco", "Consolas", "STXingkai";
}

.s-block-nav .border-for-item .nav-text .nav-text-content:hover {
	color:blue;
	 text-shadow: 0.05em 0.05em 0.02em #999999 !important;
}
/*增加鼠标放上去有边框效果*/
.s-block-nav .d-nav-item .border-for-item:hover{
height: 41px;
	border:1px solid black;
}
}
