css:
@-moz-document domain("playurbano.com") {
 html body {
    background-color: #212b24;
    color: #ffffff;
}
div.home ul li {
    background-color: #2ecc71;
   
}
#header #menu {
    
    background: #212b24;
        box-shadow: 0 0 10px 2px #2ecc71;
  
}
#header #menu li a {

    color: #fff;
}
#header #menu li {
  
    border-right: 1px solid #2dcc73;
}
#footer .widgetFooter li {
   
    background: #2ecc71;
    
    box-shadow: 0px 1px 1px #2ecc71;
}
.relacionados ul li a, .widgetMasVistos ul li a {
   
    color: #fff;
    
}
.home ul .vn {

    color: black;
}
.albumes li, .home ul li {
   
    background: #0fc6b3;
   
    box-shadow: 0px 1px 1px #000;
}
.lista_musica li {
    
    background: #096932!important;
}.lista_musica li a {
    color: #fff;
}
.lista_musica li:nth-of-type(2n) {
    background: #132d50!important;
}
#footer .widgetFooter .titulo {
   
    color: #fdfffe!important;
}
#contenido.single-album, #contenido.single-playlist {
    background: #212b24!important;
}
#contenido {

    background-color: #212b24;
}
.single .bloque_texto, .artista .bloque_texto {
    
    border-left: 1px solid #2ecc71;
}
#contenido h1.tp {
    color: #ffffff;
}
.relacionados ul li, .widgetMasVistos ul li {
  
    border-bottom: 1px dashed #2ecc71!important;
}
.home h2 {
    border-bottom: 2px ridge #2ecc71;
    
    border-top: 2px ridge #2ecc71;
}
#searchH form input[type=search] {
    
    background-color: #1d1d1d!important;
    color: #2ecc71!important
}
   #footer .copyright {
    
    color: white;
}
#contenido.single {
    background: #212b24!important;
}
.searchPage_album_video ul li a {
   
    background: #188c49!important;
   
    color: #e2e2e2!important;
}
#footer {
   
    border-top: 1px solid #2db74f!important;
}
}
