css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.popjisyo.com/WebHint/Portal_e.aspx") {
  td[width="250"],         /* right column */
  div.headerNote,          /* header */
  div.reqs,                /* requirements bar at bottom */
  table[bgcolor="white"],  /* various extra stuff */
  table[bgcolor="black"],
  table.tabTitle,
  table.tabSection,
  div.dictLink,            /* links to sources */
  td.tabInputHeader,       /* header for input area */
  table table table,       /* URL entry area */
  td > br,                 /* extra line breaks at bottom */
  span.copyright
  {
    display:none;
  }
  textarea
  {
    margin-top:-14px;      /* cover up the wasted space */
    font-size:16pt;        /* bigger font = more readable */
    height:1.3em;          /* make the textarea smaller */
  }
}
