css:
@-moz-document domain("docs.unity3d.com"), regexp("file:///.*Unity.*/Documentation/.*") {
.content, .content-wrap, #master-wrapper, body, html{
	background-color:#444;
}
.menu, .filler{
    background:transparent;
    background-color:#111;
}
div.header .more ul li a {
    border:none;
}
div.header .more .filler{
/*     background:none; */
	background-color:#111;
/*     border:none; */
    
    
}
 div.toolbar div.content{
	background-color:#252525;
}
table, td, tr{
		background-color:#333;
}
pre, .feedbackbox{
    color:#aaa;
    background-color:#333;
    border:none;
}
td::odd {
	background-color:#333;
    
}
.sidebar-menu, p{
    color:#aaa;
}
div.sidebar-wrap, div.search-results h2{
    border:none;
    background:transparent;
}
div.sidebar-menu ul li a {
	color:#aaa;
}
.mCSB_scrollTools .mCSB_dragger .mCSB_dragger_bar{
    background-color:#da6;
}
/* .mCSB_draggerRail{
    background:transparent;
    background-color:#000;
} */

a, div.search-results div.result a.title{
    color:#da6;
}
.mCSB_scrollTools .mCSB_dragger{
	background-color:#222;
    
}
.mCSB_scrollTools .mCSB_draggerRail{
    background-color:#222;
    border:none;
}
div.sidebar-menu ul li div.expanded{
	background:transparent;
/*     border:none; */
}
div.sidebar-menu ul li div.collapsed{
	background-image:none;
    background-color:#4da;

}

div.header div.search-form:before, div.header .more .filler:before , input{
    background-color:#000;
	background:transparent;
}
#q{
	background-color:#000;
    
}

}
