css:
@-moz-document domain("weletranslation.com") {
/*
* Author: Ew0345
* Version: 1.0
*/

/* Main Body */
.wrap {
    background-color: #232323 !important;
}
.container.content {
    color: #eee !important;
}

/* Chapter */
.post-title {
    color: #c1c1c1 !important;
}
.disabled-unselectable-disabled p {
    color: #eee !important;
    font-family: 'Open Sans', Helvetica, sans-serif !important;
    font-size: 18.5px !important;
}
/* Translator Note (Uncommon to see on here) */
.translator_note_header,
.translator_note {
    color: lightslategray !important;
    font-style: italic !important;
}
.sidebar-toggle {
   background-color: #505050 !important;
}
.sidebar-toggle::before {
    display: inline-block;
    width: 1rem;
    height: .75rem;
    content: "";
    background-image: -webkit-linear-gradient(to bottom, #555, #555 20%, #1f1f1f 20%, #1f1f1f 40%, #555 40%, #555 60%, #1f1f1f 60%, #1f1f1f 80%, #555 80%, #555 100%) !important;
    background-image: -moz-linear-gradient(to bottom, #555, #555 20%, #1f1f1f 20%, #1f1f1f 40%, #555 40%, #555 60%, #1f1f1f 60%, #1f1f1f 80%, #555 80%, #555 100%) !important;
    background-image: -ms-linear-gradient(to bottom, #555, #555 20%, #1f1f1f 20%, #1f1f1f 40%, #555 40%, #555 60%, #1f1f1f 60%, #1f1f1f 80%, #555 80%, #555 100%) !important;
    background-image: linear-gradient(to bottom, #555, #555 20%, #11f1f1f 20%, #1f1f1f 40%, #555 40%, #555 60%, #1f1f1f 60%, #1f1f1f 80%, #555 80%, #555 100%) !important;
}
}
