css:
@-moz-document url("https://www.alpha-orbital.com/pathfinder") {
body,
td.pathfinderSysName,
.post-content p,
h2 {
    color: #EEE;
}
.blog-list .post .post-text {
    background: rgba(0, 0, 0, .41);
}
.blog-sidebar {
    display: none;
}
#siteContent {
    width: 100%;
    left: 0;
}
#pathfinder th {
    font-size: 16px;
}
