css:
@-moz-document domain("fontawesome.io") {
.hidden-print.fa5.collapse.in,
.navbar.navbar-inverse.navbar-static-top.hidden-print,
.jumbotron.jumbotron-ad.hidden-print,
.container .hidden-print,
.footer.hidden-print,
.alert.alert-warning,
.alert.alert-success,
.text-muted{
    display: none;
}

body {
    background: #333;
}
.fontawesome-icon-list .fa-hover a {
    line-height: 65px;
	font-size: 25px;
    height: 65px;
    color: #888;
}
.fontawesome-icon-list .fa-hover a .fa {color: #FC0; font-size: 45px; margin-right: 20px;}

.page-header {
	color: #00B3A4;
}
#search-input {
	background: #666;
}

#icons section:nth-child(even) .fontawesome-icon-list .fa-hover a .fa { color: #A854A8;}
#icons section:nth-child(even) .fontawesome-icon-list .fa-hover:hover a .fa,
#icons section:nth-child(odd) .fontawesome-icon-list .fa-hover:hover a .fa {color: #FFF; text-shadow: 0 0 2px #000; font-size: 45px; margin-right: 20px;}
}
