css:
@-moz-document domain("trello.com") {
.board-wrapper{
    background: #21272b;
}
.body-board-view #header {
    background: rgb(64, 64, 64);
}
.list {
    background: #404040;
}
textarea.list-header-name.mod-list-name.js-list-name-input {
    color: white;
    letter-spacing: 2px;
    font-weight: 200;
}
.list-card-details {
    background: #22272b;    
    border-radius: 7px;
}
span.list-card-title.js-card-name {
    font-weight: 300;
    color: #dedede;
    letter-spacing: 0.5px;
}
.list-card {
    background-color: #404040;
    border-bottom: none;
}
.open-card-composer {
    border-radius: 0 0 7px 7px;
    color: #8a8a8a;
    border-top: 2px dashed #22272b;
}

}
