css:
@-moz-document domain(web.whatsapp.com) {
  ._3YewW {
    min-height: 0px;
    transition: min-height 0.5s;
  }

  ._3YewW:hover {
    min-height: 48px;
    transition: min-height 0.5s;
  }

  ._3YewW * {
    position: absolute;
    opacity: 0;
  }

  ._3YewW:hover * {
    position: relative;
    opacity: 1;
    transition: opacity 0.5s;
  }
}
