css:
@-moz-document domain("wikipedia.org") {
    /* User-only */
    #content.mw-body {
        margin-left: 0;
        border-left-width: 0;
        border-top-width: 0;
    }
    
    #footer {
        margin-left: 0;
    }
    
    #mw-head-base {
        height: 0;
    }
    #mw-navigation {
        visibility: hidden;
    }
    #searchform {
        visibility: visible;
        position: absolute;
        top: 0em;
        right: 1.5em;
    }
    .mw-indicators.mw-body-content {
        top: 2.8em;
    }
    .mw-editsection {
        display: none;
    }
}
