css:
@-moz-document url-prefix("http://docs.screeps.com/api"), url-prefix("https://docs.screeps.com/api") {
.tocify-wrapper {
	background: #222;
	border-right: 1px solid #222;
}

.tocify-wrapper li {
	color: #ccc;
}

.tocify-wrapper::-webkit-scrollbar-track {
	background-color: #222;
}

.tocify-wrapper::-webkit-scrollbar {
	width: 12px;
	background-color: #333;
}

html::-webkit-scrollbar-thumb {
	background-color: #333;
}

html::-webkit-scrollbar-track {
	background-color: #222;
}

html::-webkit-scrollbar {
	width: 12px;
	background-color: #333;
}

.tocify-wrapper::-webkit-scrollbar-thumb {
	background-color: #333;
}

li.tocify-item {
	background: #222;
}

li.tocify-item.tocify-focus {
	background: #222;
}

input#input-search.search {
	background: #333;
	border-color: #222;
	border-radius: 5px;
	padding-left: 25px;
	color: #ccc;
}

ul.search-results.visible {
	background: #333;
}

.logo {
	filter: invert(100%);
}

.content {
	background: #333;
	color: #ccc;
}

.content h1 {
	background: #333;
	border-color: #333;
}

.content h2 {
	background-image: none;
	border-color: #333;
}

.content a {
	color: #d40;
}

.content table tr:nth-child(odd) > td {
	background: #333;
	color: #ccc;
}

.content table tr:nth-child(even) > td {
	background: #333;
	color: #ccc;
}

.content table td:last-child a {
	color: #d40;
}

.content table code {
	background: #222;
	color: #c30;
	padding: 3px;
}

.content code {
	background: #222;
	color: #c30;
	padding: 3px;
}

.content table tr:last-child {
	border: none;
}

.content table td .api-arg-title {
	color: #ddd;
}

.content .api-property__cpu {
	filter: invert(100%) hue-rotate(180deg);
}
}
