css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/*
Fidget Spinner Button for Multiple Tab Handler

Multiple Tab Handler
https://addons.mozilla.org/firefox/addon/multiple-tab-handler/
*/
#multipletab_piro_sakura_ne_jp-browser-action {
    background-image: none !important;
    background-position: center !important;
    background-repeat: no-repeat !important;
    filter:opacity(60%) !important;
}
#multipletab_piro_sakura_ne_jp-browser-action:hover {
    background-color: none !important;
    background-image: none !important;
    filter:opacity(70%) !important;
}
#multipletab_piro_sakura_ne_jp-browser-action:active,
#multipletab_piro_sakura_ne_jp-browser-action[open] {
    background-color: none !important;
    background-image: none !important;
    filter:opacity(80%) !important;
    -moz-transform: rotate(1440deg) !important;
    -moz-transition-duration: 0.8s !important; 
    -moz-transition-timing-function: ease-out;
}
#multipletab_piro_sakura_ne_jp-browser-action .toolbarbutton-badge-stack {
    background: none !important;
    border: none !important;
}
