css:
@-moz-document domain(community.wanikani.com)
{
.avatar-flair {
    background-size: cover  !important;
    background-color: transparent !important;}

.avatar-flair:after {
    content: "" !important;}

.avatar-flair[title*="level-5"],
.avatar-flair[title*="level-60"] {
    background-image: url(https://emojipedia-us.s3.amazonaws.com/thumbs/120/facebook/65/sunflower_1f33b.png) !important;}

.avatar-flair[title*="level-4"],
.avatar-flair[title*="level-50"] {
    background-image: url(https://emojipedia-us.s3.amazonaws.com/thumbs/120/facebook/111/hibiscus_1f33a.png) !important;}

.avatar-flair[title*="level-3"],
.avatar-flair[title*="level-40"] {
    background-image: url(https://emojipedia-us.s3.amazonaws.com/thumbs/120/facebook/65/blossom_1f33c.png) !important;}


.avatar-flair[title*="level-2"],
.avatar-flair[title*="level-30"] {
    background-image: url(https://emojipedia-us.s3.amazonaws.com/thumbs/120/emoji-one/104/wilted-flower_1f940.png) !important;}

.avatar-flair[title*="level-1"],
.avatar-flair[title*="level-20"] {
    background-image: url(https://emojipedia-us.s3.amazonaws.com/thumbs/120/emojidex/112/rose_1f339.png) !important;}

.avatar-flair[title*="level-0"],
.avatar-flair[title*="level-10"] {
    background-image: url(https://emojipedia-us.s3.amazonaws.com/thumbs/120/apple/96/cherry-blossom_1f338.png) !important;}
}
