css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("docs.rainmeter.net") {
body {
    font-size: 17px !important;
    color: #fff !important;
    background-color: #404046 !important;
}
.docs-nav {
    background-color: #404046 !important;
}
.docs-nav h4, .docs-nav h4 a {
    color: #007bff !important;
}
    .docs-nav-block a {
    color: #fff !important;
}
    .h3, h3 {
    color: #007BFF !important;
}
    .h2, h2 {
    color: #007BFF !important;
}
    .h1, h1 {
    color: #007BFF !important;
}
pre {
    background-color: #000 !important;
    color: #fff !important;
}
pre .attr, pre .keyword, pre .css .rule .keyword, pre .winutils, pre .javascript .title, pre .nginx .title, pre .subst, pre .request, pre .status {
    color: #fff !important;
}
code {
    color: #fff !important;
    background-color: #2a6d9b !important;
}
.release-notes li {
    color: rgb(255, 255, 255) !important;
}
.release-notes b {
    color: rgb(0, 128, 255) !important;
}
.exampleprev {
    border: 1px solid #000 !important;
    background-color: #007BFF !important;
}
.card {
    background-color: rgb(0, 0, 0) !important;
}
.docs-index .card {
    color: #fff !important;
}
}
