css:
@-moz-document domain(materialui.co)  {
header, footer, .notification__close, .notification__close:after, .color_hover {
    display: none !important;
}
.container {
    top: 0;
    bottom: 0;
}
.content {
    margin: 0;
}
@media only screen and (max-width: 1366px) {
	.content.colors {
    	margin: 0 !important;
	}
}
.tblcl.caw_colors {
    width: 100vw;
}
.notification {
    bottom: 10px !important;
    top: auto !important;
    right: 10px !important;
    height: calc(27vh - 20px) !important;
    width: calc(15.7vw - 19px) !important;
    box-shadow: none !important;
    -moz-transform: translate3D(0,0,0) !important;
    -webkit-transform: translate3D(0,0,0) !important;
    transform: translate3D(0,0,0) !important;
	/*-webkit-transition-delay: 5s;
    transition-delay: 5s;*/
    opacity: 1;
    animation-fill-mode: forwards;
    -webkit-animation-fill-mode: forwards;
}
.notification.open {
    opacity: 1;
    -webkit-transition-delay: 0s !important;
    transition-delay: 0s !important;
}
.color_table td.cb:hover .color_id {
    display: inline-block !important;
}
.notification__data {
    padding: 0;
    vertical-align: middle;
    text-align: center;
}
.notification__text {
	text-align: center;
    color: #fff;
    font-size: 28px;
    font-family: -apple-system,system-ui,BlinkMacSystemFont,"Segoe UI",Roboto,Oxygen-Sans,Ubuntu,Cantarell,"Open Sans","Helvetica Neue",sans-serif,"Apple Color Emoji","Segoe UI Emoji","Segoe UI Symbol";
}
}
