css:
@-moz-document domain("workflowy.com") {
/* tags text color */
.contentTagText
{
    color: #6AA6D5;
    text-shadow: -1px -1px 0 rgba(255,255,255,0.75), 1px -1px 0 rgba(255,255,255,0.75), -1px 1px 0 rgba(255,255,255,0.75), 1px 1px 0 rgba(255,255,255,0.75);
}

/* worflowy painter colors */

.wfcolor-red, .wfcolor-orange, .wfcolor-yellow, .wfcolor-lime, .wfcolor-olive,
.wfcolor-green, .wfcolor-teal, .wfcolor-aqua, .wfcolor-blue, .wfcolor-navy,
.wfcolor-fuchsia, .wfcolor-purple, .wfcolor-maroon, .wfcolor-white {
	color: black !important; 
    background-color: transparent !important;
}

.content {  		border-left: 7px solid transparent; padding-left: 2px; }

.wfcolor-red 	{	border-left: 7px solid #f44; }
.wfcolor-orange	{	border-left: 7px solid #f84; }
.wfcolor-yellow {	border-left: 7px solid #ff4; }
.wfcolor-lime	{	border-left: 7px solid #ae4; }
.wfcolor-olive	{ 	border-left: 7px solid #8a4; }
.wfcolor-green	{   border-left: 7px solid #0c0; }
.wfcolor-teal	{   border-left: 7px solid #4ea; }
.wfcolor-aqua	{   border-left: 7px solid #4ff; }
.wfcolor-blue	{   border-left: 7px solid #4af; }
.wfcolor-navy	{   border-left: 7px solid #24f; }
.wfcolor-fuchsia{   border-left: 7px solid #f2a; }
.wfcolor-purple	{   border-left: 7px solid #a0f; }
.wfcolor-maroon	{   border-left: 7px solid #a66; }
}
