css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rd.nexusmods.com") {
    .apage > .wrapper {
        max-width: 960px;
    }
    
    nav#menu .wrap ul li > a {
        padding: 10px;
    }
    
    ul.stats {
        margin-bottom: 15px;
    }
    
    ul.stats li {
        margin: 0 90px 0 0;
    }
    
    ul.modactions li.dllabel {
        margin: 8px 8px 0 322px
    }

	.tabcontent, .article, .static .wrap:first-of-type {
        background-color: #313131;
    }
}
