css:
@-moz-document /*domain([[instance]]),*/ domain(awoo.space), domain(mastodon.social)
{
  .emojione:hover {
    width: 50px !important; /* set the width and height of the expanded emojo here */
    height: 50px !important;
    transition: all 0.3s ease-in-out !important;
    /* the 0.3s is the animation time for growing the emojo, it can be set to 0 */
  }

  .emojione {
    transition: all 0.2s ease-in-out;
    /* the 0.2s is the animation time for shrinking the emojo, it can be set to 0 */
  }
}
