css:
@-moz-document domain("vk.com") {
.im-page-pinned--date{display: none;}
.im-page-pinned--name{
    float: left;
    padding: 1px;
}

div.im-page-pinned{
    height: auto;
}

div.im-page-pinned:before {
    top: 12px;
    left: 16px;
}

a.im-page-pinned--name:after {
    content: ": ";
    margin-right: 3px;
}

.im-page--chat-header.im-page--chat-header_pinned {
    height: 83px;
}

.im-page-pinned--hide {
    position: relative;
    top: -8px;
}
}
