css:
@-moz-document domain("resetera.com") {
body {
    font-family: helvetica, arial, clean, sans-serif;
}
.messageText {
    font-family: helvetica, arial, clean, sans-serif;
}
.pageWidth {
    width: 95%;
}

.avatar img {
    width: 120px !important;
    height: 120px !important;
}

.messageList .message {
    border-bottom: 1px solid #aaa;
    padding: 0px;
}

.messageList .messageInfo {
    border-left: 1px solid #ddd;
    padding: 10px;
}

.thread_view .titleBar {
    text-align: center;
    top: 42px;
    position: relative;
    margin-bottom: 0;
    background-color: rgb(127, 81, 186);
    color: #FFF;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    padding: 3px;
}
.thread_view .titleBar h1 {
    color: #EFEFEF;
}

.thread_view .titleBar a {
 	color: #D5D5D5;
}

 
.titleBar + .pageNavLinkGroup {
    position: relative;
    top: -50px;
}

a:link, a:visited {
    color: rgb(82, 82, 82);
    text-decoration: none;
}

.PageNav a {
    color: #fff;
    background-color: rgb(51, 36, 71);
    border: none;
}

body .itemPageNav a:hover {
    color: #fff;
}

.PageNav a.currentPage {
    color: #FFF;
    background-color: rgb(127, 81, 186);
}

a.PageNavPrev, a.PageNavNext {
    color: rgb(15,15,15);
    background-color: transparent;
    padding: 1px;
    border: 1px none black;
    cursor: pointer;
    width: 19px !important;
}
}
