css:
@-moz-document domain("resetera.com") {
/*Thread List*/
.pageWidth {
    width: 95%
}
.secondRow {
    display: none;
}
.listBlock {
    padding-left: 10px;
}
.lastPost .lastPostInfo {
    padding-right: 10px;
}
.lastPost .lastPostInfo .muted a {
    color: #ddd;
}
h3.title {
    margin-left: 10px;
}

/*Posts*/
.messageList .message {
    border-bottom: 1px solid #ccc;
}
.message .messageInfo {
    border-left: 1px solid rgba(0, 0, 0, 0.05);
    padding-left: 20px;
    padding-right: 20px;
}
}
