@-moz-document domain("aukro.cz") {
.promo-banner {
    display: none;
}


li.bounce {
    -webkit-animation: none !important;
    animation: none !important;
}

li.propagation-highlight {
    border-right: none !important;
}

.product-header {
    font-weight: bold !important;
}

#aukro-capability, #payment-capability, #transfer-capability-min, .buy-title #hand, .old-price, .product-discount {display:none!important;}

.prices .buy-title span {
    color:#777!important;
}

.prices .buy-title span:nth-child(4) {
    display:none;
}
