css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("crunchyroll.com") {
#showmedia_video_player {
    height: 510px !important;
    width: 960px !important;
}

#showmedia_free_trial_signup, #showmedia_about_new {
    position: relative !important;
    top: 520px !important;
}

.showmedia-related {
    position: relative !important;
    top: 520px !important;
}
	#template_skin_leaderboard {
	margin: 0 auto 10px;
	width: 1260px !important;
	line-height: 0;
	position: relative;
	z-index: 9;
}
#template_container.template-container {
	padding: 10px;
	background: #f2f2f2;
	width: 1260px !important;
}
#welcome_left {
	float: left;
	display: inline;
	width: 70% !important;
	padding: 0 20px 0 20px;
	margin-top: 20px;
	border-right: 1px solid #bbb;
}

.welcome-crnews-info, .welcome-news-info {
	width: 80% !important;
	float: left;
}
.welcome-latest-videos {
	width: 100% !important;
}
	
#main_content {
	width: 70% !important;
}	
}
