css:
 @-moz-document regexp("https://www.vietcombank.com.vn/VCBOnlineServices/VCBPaymentGateWayP1/.*"), regexp("https://www.vietcombank.com.vn/IBanking2015/.*")
  {  
    @media screen and (max-device-width: 768px) {     
     #ctl00_Panel1, #header, .intropage, #maincontents  {
       max-width: 100%;
       padding: 0;
       margin: 0;
     }
     
     .logo {
       max-width: 100%;
       padding: 10px;
     }
     
     #header .logo img {
       width: 100%;
     }
     
    .welcome, iframe, .box-support-service, .full-slider, .menu-top {
      display: none;
    }
      
      .full-site, .body-main {
        min-height: 480px;
      }

    .intro-sidebar {
      float: left;
      max-width: 100%;
      background: none;
      padding: 10px;
      margin: 10px 5px;
      border: 1px solid #AFAFAF;
      border-radius: 5px;
      width: 90%;
      font-size: 1em;
      vertical-align: middle;
      text-align: justify;
    } 
      .full-content-login, .full-content-login > div {
        top: 0;        
        float: left;
      }
      
      .full-content-login > div {
        max-width: 100%;
      }
      
      .body-main .box-login {
        top: 0;
        width: 100%;
        max-width: 100%;
        padding: 5px 10px;
        float: left;
      }
      
      .body-main .content-login {
        padding: 0 10px;
        width: inherit;
      }
      
     .tbllogin {
       width: 100%;
     }
     
     #ctl00_Content_Login_Form_Captcha_Captcha, #captchaImage {
       width: 60%;
     }
     
     
     .tbllogin .txtlogin, input[type="text"], .info-login .control input, #txtcapcha {
       width: 90%;
       border-radius: 2px; 
       border: 1px solid #AAA;       
       padding: 5px;
     }
      
      .text-captcha {
        width: 100%;
        display: block;
      }
      
      .info-login .control input{
        line-height: 2em;
      }
     
     .tbllogin .txtlogin:focus, input[type="text"]:focus {
        box-shadow: -1px -1px 15px 0px rgba(224,186,146,1);
     }
     
     tr:first-child .PageTitle, span.PageTitle {
       height: 30px;
       padding: 5px;
       text-align: center;
     }
     
     .ColunmLabel, .ColunmContent {
       padding: 2px 5px;
     }
     
     #ctl00_Content_Login_Form_LoginBtn, .btn, #btndangnhap {       
       padding: 10px;
       border: none;
       border-radius: 5px;
     }
  }
}
