css:
@-moz-document domain("tinder.com") {
.recsToolbar, .recsToolbar * {
	transition: unset !important;
	-webkit-transition: unset !important;
}
.recsToolbar {
	position: absolute;
	top: unset !important;
	bottom: -30px !important;
	z-index: 100 !important;
	pointer-events: none;
	left: calc(-50vh + 321.14px);
}
.recsToolbar:not(.recsToolbar--open) {
	left: -50vh;
}
.recsToolbar > div > span {
	pointer-events: auto;
}
.recsCardboard {
	max-width: calc(100vh - 5px - 70px - 35px) !important;
	height: 10000px;
	max-height: calc(100vh - 5px);
}
.recProfile .profileCard {
	width: calc(65vh - 5px) !important;
	height: 10000px;
	max-height: calc(100vh - 8px) !important;
	margin-top: 0 !important;
}
.profilePage .profileCard {
	width: calc(60vh - 36px) !important;
	height: 10000px;
	max-height: calc(100vh - 30px) !important;
	margin-top: 22px !important;
}
/* Expose pics */
.profileCard__slider__imgShadow {
	height: 10px !important;
	top: unset !important;
}
.profileCard__slider__img {
	z-index: unset !important;
}
}
