css:
@-moz-document domain("slack.com") {
/*
 * Message input and message items
 */
#message-input, .c-message {
    direction: rtl;
    font-family: 'Iran Sans', 'IranSans', 'Iran', 'Droid Arabic Naskh', Tahoma, sans-serif !important;
    font-size: 15px !important;
}

/* code blocks */
code, pre {
    direction: ltr;
    text-align: left;
    unicode-bidi: embed;
}

/* user avatar images */
.c-message__gutter, .c-message__label__gutter {
    float: right;
    padding-right: 10px;
}

.c-message {
    padding: .25rem 3rem .1rem 2rem !important;
}

.c-message--light .c-message__content {
    margin-right: 72px
}

.ql-editor p {
    float: right;
    text-align: right;
    direction: rtl;
    font-family: 'Iran Sans', 'IranSans', 'Iran', 'Droid Arabic Naskh', Tahoma, sans-serif !important;
}

.c-message__actions {
    right: 350px
}
}
