css:
@-moz-document domain("1ty.me") {
#main {background: #c86}

.note_box {
    background: #1b1b1b;
	color: #c86;
    font: 400 22px Calibri, Geneva, Tahoma, sans-serif;
    letter-spacing: .5px;
}

.btn-success {filter: hue-rotate(52deg)}

h1:before {
    content: '';
    display: block;
    position: fixed;
    z-index: 9999;
	width: 240px;
    height: 240px;
    background: url('https://i.imgur.com/NzBLIxn.gif');
    left: 33%;
    top: 16px;
    opacity: .6667;
    filter: blur(1px)
}

.container >.row:first-child >div * {color: #c86 !important}
.container >.row:first-child span {display: none}

}
