css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iconfinder.net") {
  #menuDiv {display: none !important;}
  #searchHeader {height: 40px !important;}

  ins {display: none !important;}

  #mainContent .settings {padding-top: 4px !important;}

  #searchResults > h1 {margin: -5px 0 0 !important; font-size: 14pt !important;}
  #searchResults > h2 {margin: 3px 0 10px !important;}
  #searchResults > .resultPages {margin-top: 3px !important;}
  #searchResults .icon {margin: 0 10px 10px !important;}

  #searchResults + div {display: none !important;}

  #footer {height: inherit !important;}
}
