css:
@-moz-document url-prefix("https://www.binance.com/tradeDetail.html") {
:root {
	--trading-pair-left-offset: 300px;
  --background-color-main: #151a1e;
  --binance-yellow: #dfad26;
}

.logo1 img {
 filter: grayscale(); 
}

/* Trading pair selection */
.selectsym {
	font-weight: bold;
 	background-color: rgba(0,0,0,0.2);
  color: var(--binance-yellow);
  font-size: 22px;
}   
    
/* Trading pair selection */
.selectsym {
    position: absolute;
    left: var(--trading-pair-left-offset);
}
/* Trading pair selection pane */
.products {
 	right: auto;
	left: calc(0px - var(--trading-pair-left-offset));
}

/* 24h % change */
.header tbody > tr:nth-child(2) > td.f-center.ng-binding:nth-child(2) {
    font-weight: bold;
    font-size: 18px;
    transform: translateY(3px);
}

/* My orders / trades / funds pane */
.detail-lb {
	background: var(--background-color-main); 
}

.table-strip tr:nth-child(odd) {
    background-color: #212830
}
.table-strip tr:nth-child(even) {
    background-color: #1c2226;
}

/* Market orders / trades pane */
/*
.detail-rt {
 	background: transparent;   
}
*/

/* Market orders inner box */
.tradefive-inner.depths-wrap {
 	background: var(--background-color-main); 
}

#chart_container {
  background: var(--background-color-main);
}

/* Limit/Market order pane */
.total-div {
 	font-size: 15px; 
}
}
