css:
@-moz-document domain("youtube.com") {
.html5-play-progress,
.ytp-play-progress {
    background: url("http://gamefignya.ucoz.com/img/road.png") repeat-x !important;
    background-size: contain !important;
    resize: both !important;
}
.html5-scrubber-button,
.ytp-scrubber-button {
    background: url('http://gamefignya.ucoz.com/img/car.png') !important;
    width: 48px !important;
    height: 24px !important;
    background-size: 48px 24px !important;
    border: none !important;
    margin-left: -16px !important;
    margin-top: -5px !important;
}
.html5-progress-bar-container,
.ytp-progress-bar-container {
    height: 12px !important;
}
.html5-progress-bar,
.ytp-progress-bar {
    margin-top: 12px !important;
}
.html5-progress-list,
.ytp-progress-list,
.video-ads .html5-progress-list.html5-ad-progress-list,
.video-ads .ytp-progress-list.ytp-ad-progress-list {
    height: 12px !important;
}
.ytp-volume-slider-track {
    background: #0C4177 !important;
}
}
